int MyTree::handle(int e) {
  //
  if ( Fl::event_button() == FL_RIGHT_MOUSE  && e == FL_RELEASE ) {
  
  
  	Fl_Tree_Item *fcus = get_item_focus();
  	Fl_Tree_Item *item = first_selected_item();
  	std::cout << "fcus: \t" << fcus << std::endl ;
  	std::cout << "Last: \t" << item << std::endl ;
  
  	std::cout << "Mouse: \tx:" << Fl::event_x() << "y: \t" << Fl::event_y() << std::endl ;
  	int mouse_y = Fl::event_y();
  	for ( Fl_Tree_Item *i = first(); i; i = next(i) ) {
  
  		bool item_found = false;
  		if (  mouse_y > i->y() && mouse_y <= (i->y() + i->h())     ) {
  			printf("Item: %s\n", i->label());
  			set_item_focus(i);
  			item_clicked(i);
  			deselect_all(); // deselect all items
  			select(i); // select this one
  
  			redraw();
  			set_changed();
  			do_callback((Fl_Widget*)this, user_data());
  
  
  			Fl_Menu_Item rclick_menu[] = {
  				{ "Copy",   0, Copy_CB,  (void*)this },
  				{ "Paste",  0, Paste_CB, (void*)this },
  				{ 0 }
  				};
  			const Fl_Menu_Item *m = rclick_menu->popup(Fl::event_x(), Fl::event_y(), 0, 0, 0);
  			if ( m ) m->do_callback(0, m->user_data());
  			return(1);          // (tells caller we handled this event)
  
  			break;
  			}
  		}
  	return(1);
  	}
  return Fl_Tree::handle(e); // let Fl_Input handle all other events
}

