static void RebuildTree() {
  tree->clear();
  tree->add("Aaa");
  tree->add("Bbb");
  tree->add("Ccc");
  tree->add("Ddd");
  tree->add("Bbb/child-01");
  tree->add("Bbb/child-01/111");
  tree->add("Bbb/child-01/222");
  tree->add("Bbb/child-01/333");
  tree->redraw();
}

static void cb_tree(MyTree*, void*) {
  Fl_Tree_Item *item = tree->item_clicked();
  fprintf(stderr, "TREE CALLBACK: label='%s' userdata=%ld\n",
  item->label(),
  (long)tree->user_data());
}

/**
 This window callback allows the user to save & exit, don't save, or cancel.
*/
void re4_util_ui_close() {
  // This window callback allows the user to save & exit, don't save, or cancel.
  
  int result = fl_choice("Do you want to save before quitting?",
      "Don't Save",  // 0
      "Save",        // 1
      "Cancel"       // 2
      );
  switch (result) {
      case 0: { // Close without saving
      
          // CleanUp
          if (app != nullptr) {delete app;}
          if (ini != nullptr) {delete ini;}
      
          exit(0);
          break;
          }
      case 1: { // Save and close
      
          // CleanUp
          if (app != nullptr) {delete app;}
          if (ini != nullptr) {delete ini;}
  
          exit(0);
          break;
          }
      case 2: { // Cancel / don't close
  
  
          break;
          }
      }
}

